import React,{useState} from "react";
import axios from "axios";
export default ({postId})=>{
   const [FName,updateFname]= useState("FName is Empty")
   const [rollNo,updaterollNO]= useState("rollNo is Empty")
   const [Contact,updateContact]= useState("Contact is Empty")
   const handleMySubmit= async(e) =>{
       e.preventDefault();
       await axios.post(`http://localhost:4002/api/v1/blog/adduser`,{FName,rollNo,Contact}).catch(e=>console.log(e.message))
       updateFname('')
       updateContact('')
       updaterollNO('')
   }

   return(
        <form onSubmit={handleMySubmit}>
            <div>
            <label className="form-group">Full Name</label>
            <input type="text" value={FName} onChange={(e)=>updateFname(e.target.value)} className="form-control"/><br /><br />
            <label className="form-group">Roll Number</label>&nbsp;&nbsp;&nbsp;&nbsp;
            <input type="text" value={rollNo} onChange={(e)=>updaterollNO(e.target.value)} className="form-control"/><br /><br />
            <label className="form-group">Full Name</label>&nbsp;&nbsp;&nbsp;&nbsp;
            <input type="text" value={Contact} onChange={(e)=>updateContact(e.target.value)} className="form-control"/><br /><br />
            </div> Fullname:&nbsp;&nbsp;&nbsp;&nbsp;{FName}<br/>RollNo:&nbsp;&nbsp;&nbsp;&nbsp;{rollNo}<br/>Mobile:&nbsp;&nbsp;&nbsp;&nbsp;{Contact}<br/>
            
            <button className="btn btn-primary">submit</button>
           
        </form>
    )
}
